import Atak
import cardan4
import json

# przy tych parametrach 10 atakow zajelo mi 7 minut
is_spanish = False
grill_size = 14
text_length = 3000  # jezeli tekst nie zmiesci sie w grillu to kryptotekst bedzie dluzszy

if __name__ == '__main__':
    file = open('wyniki3.txt', 'w', encoding='utf-8')
    attacks = 10

    solutions = list()
    for i in range(attacks):
        kt = cardan4.main(is_spanish, text_length, grill_size)
        solution = Atak.main()

        solutions.append(solution)

    solutions = sorted(solutions, key=lambda x: x[0])

    for i in range(attacks):
        json_data = json.dumps(solutions[i], ensure_ascii=False)

        file.write(''.join(json_data))
        file.write('\n')
        file.write('\n')

